import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap, Params } from '@angular/router';
import { IContact } from 'src/app/models/IContact';
import { IGroup } from 'src/app/models/IGroup';
import { ContactService } from 'src/app/services/contact.service';


@Component({
  selector: 'app-view-contact',
  templateUrl: './view-contact.component.html',
  styleUrls: ['./view-contact.component.css']
})
export class ViewContactComponent implements OnInit{
  public loading:boolean = false;
  public contactId: string | null = null;
  public contact: IContact = {} as IContact;
  public errorMessage: string | null = null;
  public group:IGroup = {} as IGroup;


  constructor(private activatedRoute : ActivatedRoute,
    private contactService: ContactService){

  }

  ngOnInit(): void {
      this.activatedRoute.paramMap.subscribe((param : ParamMap) => {
        this.contactId = param.get('contactId')
      });
      if(this.contactId){
        this.loading = true;
        this.contactService.getContact(this.contactId).subscribe((data:IContact) =>{
          this.contact = data;
          this.loading = false;
          this.contactService.getGroup(data).subscribe((data: IGroup) => {
            this.group = data;
          })
        }, (error) => {
          this.errorMessage = error;
          this.loading = false;
        });
      }
  }

  public isNotEmpty(){
    return Object.keys(this.contact).length > 0 && Object.keys(this.group).length > 0;
  }
}
<div class="container mt-3">
  <div class="row">
    <div class="col">
      <p class="h3 fw-bold text-warning">View Contact</p>
      <p class="fst-italic">
        Lorem ipsum dolor sit, amet consectetur adipisicing elit. Beatae modi et
        maxime, est exercitationem aliquam fuga at quae illo consequuntur,
        delectus perspiciatis laudantium corporis hic aut officiis odit.
        Recusandae, asperiores.
      </p>
    </div>
  </div>
</div>

<!-- Loading Spinner -->
<div *ngIf="loading">
  <app-spinner></app-spinner>
</div>

<!-- Error Message -->
<div *ngIf="!loading && errorMessage">
  <div class="container">
    <div class="row">
      <div class="col">
        <p class="h4 text-danger-fw">{{ errorMessage }}</p>
      </div>
    </div>
  </div>
</div>

<!--Contact Details-->
<div *ngIf="!loading && isNotEmpty()" class="container">
  <div class="row align-items-center">
    <div class="col-sm-4">
      <img class="contact-img-big" alt="" [src]="contact.photo" />
    </div>
    <div class="col-sm-8">
      <ul class="list-group">
        <li class="list-group-item list-group-item-warning">
          Name : <span class="fw-bold">{{ contact.name }}</span>
        </li>
        <li class="list-group-item list-group-item-warning">
          Email : <span class="fw-bold">{{ contact.email }}</span>
        </li>
        <li class="list-group-item list-group-item-warning">
          Mobile : <span class="fw-bold">{{ contact.mobile }}</span>
        </li>
        <li class="list-group-item list-group-item-warning">
          Company : <span class="fw-bold">{{ contact.company }}</span>
        </li>
        <li class="list-group-item list-group-item-warning">
          Title : <span class="fw-bold">{{ contact.title }}</span>
        </li>
        <li class="list-group-item list-group-item-warning">
          Group Name : <span class="fw-bold">{{ group.name }}</span>
        </li>
      </ul>
    </div>
  </div>
</div>

<div class="row mt-3">
  <div class="col">
    <a routerLink="/" class="btn btn-danger ms-5">
      <i class="fa fa-arrow-alt-circle-left"></i> Back
    </a>
  </div>
</div>
